// Enhanced email extraction - get sender email, message content, and classify message type
const messages = $input.all();
const emailList = [];

// Function to classify message type based on content
function classifyMessageType(subject, body) {
  const content = (subject + ' ' + body).toLowerCase();

  // Price inquiry keywords
  const priceKeywords = [
    'price', 'cost', 'quote', 'pricing', 'budget', 'fee', 'rate', 'charge',
    'expensive', 'cheap', 'affordable', 'money', 'payment', 'bill',
    'estimate', 'quotation', 'how much', 'what does it cost', 'price list'
  ];

  // General inquiry keywords
  const generalKeywords = [
    'information', 'details', 'tell me about', 'interested in', 'learn more',
    'available', 'service', 'product', 'help', 'support', 'question',
    'inquiry', 'enquiry', 'more info', 'what is', 'how does', 'can you'
  ];

  // Complaint/issue keywords
  const complaintKeywords = [
    'problem', 'issue', 'complaint', 'not working', 'broken', 'error',
    'disappointed', 'unhappy', 'refund', 'cancel', 'unsatisfied'
  ];

  // Booking/appointment keywords
  const bookingKeywords = [
    'appointment', 'booking', 'schedule', 'reserve', 'book', 'meeting',
    'consultation', 'session', 'visit', 'time slot'
  ];

  // Check for price inquiry
  if (priceKeywords.some(keyword => content.includes(keyword))) {
    return 'Price Inquiry';
  }

  // Check for complaint
  if (complaintKeywords.some(keyword => content.includes(keyword))) {
    return 'Complaint/Issue';
  }

  // Check for booking
  if (bookingKeywords.some(keyword => content.includes(keyword))) {
    return 'Booking/Appointment';
  }

  // Check for general inquiry
  if (generalKeywords.some(keyword => content.includes(keyword))) {
    return 'General Inquiry';
  }

  // Default classification
  return 'Other';
}

// Function to extract email body text
function extractEmailBody(msg) {
  let body = '';

  // Try different ways to get the email body
  if (msg.snippet) {
    body = msg.snippet;
  } else if (msg.payload) {
    if (msg.payload.body && msg.payload.body.data) {
      // Decode base64 if needed
      try {
        body = Buffer.from(msg.payload.body.data, 'base64').toString('utf-8');
      } catch (e) {
        body = msg.payload.body.data;
      }
    } else if (msg.payload.parts) {
      // Handle multipart messages
      for (const part of msg.payload.parts) {
        if (part.mimeType === 'text/plain' && part.body && part.body.data) {
          try {
            body = Buffer.from(part.body.data, 'base64').toString('utf-8');
            break;
          } catch (e) {
            // Continue to next part
          }
        }
      }
    }
  }

  // Clean up the body text
  return body.replace(/\r?\n/g, ' ').trim();
}

// Function to extract subject
function extractSubject(msg) {
  if (msg.subject) {
    return msg.subject;
  } else if (msg.payload && msg.payload.headers) {
    const subjectHeader = msg.payload.headers.find(h => 
      h.name && h.name.toLowerCase() === 'subject'
    );
    return subjectHeader ? subjectHeader.value : '';
  }
  return '';
}

messages.forEach(message => {
  const msg = message.json;
  let senderEmail = null;

  // Try to get email from From field first
  if (msg.From) {
    senderEmail = msg.From;
  } else if (msg.payload && msg.payload.headers) {
    // Get from headers if From field is not available
    const fromHeader = msg.payload.headers.find(h => 
      h.name && h.name.toLowerCase() === 'from'
    );
    if (fromHeader) {
      senderEmail = fromHeader.value;
    }
  }

  if (senderEmail) {
    // Clean email - extract just the email address part
    const emailMatch = senderEmail.match(/<([^>]+)>/);
    const cleanEmail = emailMatch ? emailMatch[1] : senderEmail;

    // Extract message content
    const subject = extractSubject(msg);
    const body = extractEmailBody(msg);
    const messageType = classifyMessageType(subject, body);

    emailList.push({
      messageId: msg.id,
      senderEmail: cleanEmail.toLowerCase().trim(),
      subject: subject,
      messageBody: body,
      messageType: messageType,
      receivedTime: new Date().toISOString(),
      // Keep original sender format for reference
      originalSender: senderEmail
    });
  }
});

return emailList;

// Enhanced Simple Email Match - passes through all message data
// This will ONLY return items that have matches, so no IF node needed

const newEmails = $('Extract Sender Emails').all().map(item => item.json);
const allLeads = $input.all().map(item => item.json);

// Filter leads that haven't replied yet
const unrepliedLeads = allLeads.filter(lead => {
  const replied = lead.Replied;
  return !replied || replied === '' || replied.toLowerCase() === 'no';
});

const matches = [];

// Check each new email against our lead list
newEmails.forEach(email => {
  const matchingLead = unrepliedLeads.find(lead => {
    if (!lead.Email) return false;
    return lead.Email.toLowerCase().trim() === email.senderEmail;
  });

  if (matchingLead) {
    matches.push({
      leadEmail: matchingLead.Email,
      leadName: matchingLead['Full Name'] || 'Unknown',
      messageId: email.messageId,
      receivedTime: email.receivedTime,
      // Pass through all message data from Extract Sender Emails node
      subject: email.subject,
      messageBody: email.messageBody,
      messageType: email.messageType,
      originalSender: email.originalSender,
      senderEmail: email.senderEmail
    });
  }
});

// Only return matches - if no matches, return empty array (stops the workflow)
return matches;

// Extract WhatsApp message from WaSender webhook
const webhookData = $input.first().json;

// Get the message data from the webhook structure
const messageData = webhookData.body.data.messages;

// Skip if message is from me
if (messageData.key.fromMe) {
  return null;
}

// Extract message body from conversation
let messageBody = '';
if (messageData.message && messageData.message.conversation) {
  messageBody = messageData.message.conversation;
}
  // Check for extended text message (like your example)
else if (messageData.message.extendedTextMessage && messageData.message.extendedTextMessage.text) {
    messageBody = messageData.message.extendedTextMessage.text;
  }

else {
  messageBody = 'No message found';
}

// Extract phone number and clean it
let whatsappFrom = messageData.key.remoteJid.replace('@s.whatsapp.net', '');
if (!whatsappFrom.startsWith('+')) {
  whatsappFrom = '+' + whatsappFrom;
}

// Extract sender name
const senderName = messageData.pushName || 'Unknown';

// Get sessionId from webhook
const sessionId = webhookData.body.sessionId;

// Get timestamp
const receivedTime = new Date(webhookData.body.timestamp).toISOString();

// Simple message type detection
const messageText = String(messageBody).toLowerCase();
let messageType = 'general';

if (messageText.includes('price') || messageText.includes('pricing') || messageText.includes('cost') || messageText.includes('how much') || messageText.includes('quote') || messageText.includes('estimate') || messageText.includes('rates') || messageText.includes('fees') || messageText.includes('budget') || messageText.includes('expensive') || messageText.includes('cheap')) {
  messageType = 'pricing';
} else if (messageText.includes('yes') || messageText.includes('interested') || messageText.includes('book') || messageText.includes('schedule') || messageText.includes('appointment') || messageText.includes('call me') || messageText.includes('sounds good') || messageText.includes('lets do it') || messageText.includes('when can') || messageText.includes('available')) {
  messageType = 'positive';
} else if (messageText.includes('problem') || messageText.includes('issue') || messageText.includes('complaint') || messageText.includes('not working') || messageText.includes('broken') || messageText.includes('error') || messageText.includes('help') || messageText.includes('fix') || messageText.includes('repair')) {
  messageType = 'complaint';
} else if (messageText.includes('thank') || messageText.includes('thanks') || messageText.includes('appreciate') || messageText.includes('great job') || messageText.includes('excellent') || messageText.includes('amazing') || messageText.includes('perfect') || messageText.includes('love it')) {
  messageType = 'appreciation';
} else if (messageText.includes('hello') || messageText.includes('hi') || messageText.includes('hey') || messageText.includes('good morning') || messageText.includes('good afternoon') || messageText.includes('good evening') || messageText.includes('how are you') || messageText.includes('whats up')) {
  messageType = 'greeting';
} else if (messageText.includes('cancel') || messageText.includes('refund') || messageText.includes('not interested') || messageText.includes('no thanks') || messageText.includes('dont want') || messageText.includes('stop') || messageText.includes('unsubscribe')) {
  messageType = 'negative';
} else if (messageText.includes('location') || messageText.includes('address') || messageText.includes('where are you') || messageText.includes('directions') || messageText.includes('how to reach') || messageText.includes('map')) {
  messageType = 'location';
} else if (messageText.includes('hours') || messageText.includes('open') || messageText.includes('closed') || messageText.includes('timing') || messageText.includes('what time') || messageText.includes('when do you')) {
  messageType = 'hours';
}

return {
  whatsappFrom: whatsappFrom,
  senderName: senderName,
  messageBody: messageBody,
  receivedTime: receivedTime,
  messageType: messageType,
  messageId: messageData.key.id,
  sessionId: sessionId
};

// Match WhatsApp reply with lead data
const whatsappReply = $('Parse WhatsApp Reply Data').first().json;
const allLeads = $input.all().map(item => item.json);

// Skip if no WhatsApp data (filtered out message)
if (!whatsappReply) {
  return null;
}

// Find matching lead by phone number
const matchingLead = allLeads.find(lead => {
  if (!lead['Phone Numbers']) return false;

  // Clean lead phone number for comparison
  let leadPhone = lead['Phone Numbers'].toString();
  leadPhone = leadPhone.replace(/[^0-9]/g, ''); // Remove all non-digits

  // Clean WhatsApp phone for comparison
  let whatsappPhone = whatsappReply.whatsappFrom.replace(/[^0-9]/g, '');

  // Match last 10 digits (handles country codes)
  const leadLast10 = leadPhone.slice(-10);
  const whatsappLast10 = whatsappPhone.slice(-10);

  return leadLast10 === whatsappLast10;
});

if (matchingLead) {
  return {
    matchFound: true,
    leadEmail: matchingLead.Email,
    leadName: matchingLead['Full Name'] || 'Customer',
    leadPhone: matchingLead['Phone Numbers'],
    whatsappReplyData: whatsappReply
  };
} else {
  return {
    matchFound: false,
    whatsappReplyData: whatsappReply,
    reason: 'No matching lead found for WhatsApp: ' + whatsappReply.whatsappFrom
  };
}

